	/**
	 * 金农市场分析详细页面
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return
	 */
	public ActionForward toMarAnalysisLoad(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		String type = request.getParameter("type");// 取出不同的操作标志
		request.setAttribute("opertype", type);
		
		if("insert".equals(type)) {
			return mapping.findForward("load");
		}
		if("detail".equals(type)) {
			String id = request.getParameter("id");
			IBaseDTO dto = mas.getMarAnalysisInfo(id);
			request.setAttribute(mapping.getName(), dto);
			
			return mapping.findForward("load");
		} else if("update".equals(type)) {
			String id = request.getParameter("id");
			IBaseDTO dto = mas.getMarAnalysisInfo(id);
			request.setAttribute(mapping.getName(), dto);
			
			return mapping.findForward("load");
		} else if("delete".equals(type)) {
			String id = request.getParameter("id");
			IBaseDTO dto = mas.getMarAnalysisInfo(id);
			request.setAttribute(mapping.getName(), dto);
			
			return mapping.findForward("load");
		}
		else if("toWord".equals(type))
		{
			String id = request.getParameter("id");
			IBaseDTO dto = mas.getMarAnalysisInfo(id);
			request.setAttribute("marDto", dto);
			//System.out.println(dto.get("outlook"));
			request.setAttribute(mapping.getName(), dto);
			return new ActionForward("/screen/marAnalysis/marAnalysisWord.jsp"); 
			//return mapping.findForward("toWord");
		}
		return mapping.findForward("load");
	}
	
	/**
	 * 金农市场分析操作
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return
	 */
	public ActionForward toMarAnalysisOper(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		DynaActionFormDTO dto = (DynaActionFormDTO)form;
		
		String type = request.getParameter("type");// 取出不同的操作标志
		request.setAttribute("opertype", type);
		
		if("insert".equals(type)) {
			boolean flag = mas.addMarketAnalysis(dto);
			if(flag) {
				request.setAttribute("operSign", "insertsuccess");
				dto.set("id", "");
				dto.set("hotlineContent", "");
				request.setAttribute(mapping.getName(), dto);
				return mapping.findForward("load");
			}
		}
		if("update".equals(type)) {
			boolean flag = mas.updateMarketAnalysis(dto);
			if(flag) {
				request.setAttribute("operSign", "updatesuccess");
				return mapping.findForward("load");
			}
		}
		if("delete".equals(type)) {
			boolean flag = mas.deleteMarketAnalysis(dto);
			if(flag) {
				request.setAttribute("operSign", "deletesuccess");
				return mapping.findForward("load");
			}
		}
		return mapping.findForward("load");
	}

	public HotlineService getMas() {
		return mas;
	}

	public void setMas(HotlineService mas) {
		this.mas = mas;
	}

	
